	@Autowired
	StateMapper stateMapper;

	@Autowired
	StateRepository stateRepository;

	@Autowired
	JdbcTemplate jdbcTemplate;

	@Override
	public String addState(StateModel stateModel) throws Exception {
		StateDomain stateDomain = new StateDomain();
		BeanUtils.copyProperties(stateModel, stateDomain);
		List<StateDomain> StateDomain = stateRepository.findAll();
		if (StateDomain.size() == 0) {
			String sql = insertState();
			int res = jdbcTemplate.update(sql);
			if (res == 1) {
				System.out.println("success");
			} else {
				System.out.println("failure");
			}
		}
		return stateDAO.addState(stateDomain);
	}

	@Override
	public List<StateDomain> getStateList() throws Exception {
		List<StateDomain> stateDomain = stateDAO.getStateList();
		return stateDomain;
	}

	@Override
	public StateDomain getStateListById(long stateId) throws Exception {
		StateDomain stateDomain = stateDAO.getStateListById(stateId);
		return stateDomain;
	}

	@Override
	public List<StateDomain> getStatedetails(long countryId) throws Exception {
		List<StateDomain> stateDomain = stateDAO.getStatedetails(countryId);
		return stateDomain;
	}

	@Override
	public String updateState(StateModel stateModel) throws Exception {
		StateDomain stateDomain = new StateDomain();
		BeanUtils.copyProperties(stateModel, stateDomain);
		return stateDAO.updateState(stateDomain);
	}

	public String deleteStateById(long stateId) throws Exception {
		return stateDAO.deleteStateById(stateId);
	}

